sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();
var later = new GlideDateTime();
var time24hrs = new GlideTime();
time24hrs.setValue("24:00:00");
later.add(time24hrs);
gs.info(rightNow);
gs.info(later);
// Query the database for NeedIt Task records with Due date field values older
// then the current time.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var nITasks = new GlideRecord('x_58872_needit_needit_task');
nITasks.addQuery('due_date','>=',rightNow);
nITasks.addQuery('due_date','<=', later);
nITasks.addQuery('state','!=',3);

nITasks.query();
// Write a log message for each overdue NeedIt Task Record
while(nITasks.next()){
	gs.info("NeedIt Tasks due soon = " + nITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', nITasks, nITasks.number, gs.getUserName());
}
